mapper.select:
select * from t_recieve_items


		and rid = #{rid}


		and rcode like '%{rcode}%'


		and rname like '%{rname}%'


		and wid = #{wid}


		and wcode like '%{wcode}%'


		and wname like '%{wname}%'


		and gid = #{gid}


		and gcode like '%{gcode}%'


		and gname like '%{gname}%'


		and sid = #{sid}


		and scode like '%{scode}%'


		and sname like '%{sname}%'
